  `Weixin` varchar(20) NOT NULL DEFAULT '' COMMENT '微信',
  `Address` varchar(60) NOT NULL DEFAULT '' COMMENT '住址',
  `Roles` varchar(200) NOT NULL DEFAULT '' COMMENT '角色',
  `Remark` varchar(200) NOT NULL DEFAULT '' COMMENT '备注',
